import React, { useState, useEffect } from 'react';

const Account = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [message, setMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setMessage('You must be logged in to access this page.');
          return;
        }

        const response = await fetch('/api/account', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || 'Failed to fetch account details.');
        }

        const data = await response.json();
        setFormData({ ...formData, name: data.name, email: data.email });
      } catch (error) {
        console.error('Error fetching user data:', error);
        setMessage(error.message);
      }
    };

    fetchUserData();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    setMessage('');
    setSuccessMessage('');

    if (formData.password && formData.password !== formData.confirmPassword) {
      setMessage('Passwords do not match.');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('You must be logged in to update your account.');

      const response = await fetch('/api/account', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to update account.');
      }

      setSuccessMessage('Account updated successfully!');
    } catch (error) {
      console.error('Update error:', error);
      setMessage(error.message);
    }
  };

  const handleDelete = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('You must be logged in to delete your account.');

      const confirmed = window.confirm('Are you sure you want to delete your account? This action cannot be undone.');
      if (!confirmed) return;

      const response = await fetch('/api/account', {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to delete account.');
      }

      localStorage.removeItem('token');
      window.location.href = '/';
    } catch (error) {
      console.error('Delete error:', error);
      setMessage(error.message);
    }
  };

  return (
    <div className="account-page">
      <h1>My Account</h1>
      {message && <p className="error-message">{message}</p>}
      {successMessage && <p className="success-message">{successMessage}</p>}
      <form onSubmit={handleUpdate}>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Name"
          required
        />
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Email"
          required
        />
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="New Password"
        />
        <input
          type="password"
          name="confirmPassword"
          value={formData.confirmPassword}
          onChange={handleChange}
          placeholder="Confirm New Password"
        />
        <button type="submit">Update</button>
      </form>
      <button onClick={handleDelete}>Delete Account</button>
    </div>
  );
};

export default Account;
