import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Menu = () => {
  // State to store menu items
  const [menuItems, setMenuItems] = useState([]);
  const [newItem, setNewItem] = useState({ name: '', description: '', price: '' });

  // Fetch menu items on component mount
  useEffect(() => {
    axios.get('/api/menu')
      .then(response => {
        setMenuItems(response.data); // Populate the state with fetched menu items
      })
      .catch(error => {
        console.error('Error fetching menu items:', error);
      });
  }, []);

  // Handle form submission to add a new menu item
  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('/api/menu', newItem)
      .then(response => {
        setMenuItems([...menuItems, { id: response.data.id, ...newItem }]); // Add new item to the state
        setNewItem({ name: '', description: '', price: '' }); // Clear form after submission
      })
      .catch(error => {
        console.error('Error adding menu item:', error);
      });
  };

  // Handle menu item deletion
  const handleDelete = (id) => {
    axios.delete(`/api/menu/${id}`)
      .then(() => {
        setMenuItems(menuItems.filter(item => item.id !== id)); // Remove item from state
      })
      .catch(error => {
        console.error('Error deleting menu item:', error);
      });
  };

  return (
    <section className="menu-section py-5">
      <div className="container">
        <h2 className="text-center">Our Menu</h2>
        <div className="row g-4 mt-4">
          {/* Display menu items dynamically */}
          {menuItems.map((item) => (
            <div className="col-lg-6 col-md-12" key={item.id}>
              <img src="/assets/DSC_9138.jpg" className="img-fluid rounded" alt={item.name} />
              <h4 className="mt-3">{item.name}</h4>
              <p>{item.description}</p>
              <p>Price: ${item.price}</p>
              <button className="btn btn-danger" onClick={() => handleDelete(item.id)}>Delete</button>
            </div>
          ))}
        </div>

        {/* Form to add a new item */}
        <div className="mt-4">
          <h3>Add New Menu Item</h3>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Dish Name</label>
              <input
                type="text"
                className="form-control"
                id="name"
                value={newItem.name}
                onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">Description</label>
              <input
                type="text"
                className="form-control"
                id="description"
                value={newItem.description}
                onChange={(e) => setNewItem({ ...newItem, description: e.target.value })}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="price" className="form-label">Price</label>
              <input
                type="number"
                className="form-control"
                id="price"
                value={newItem.price}
                onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">Add Item</button>
          </form>
        </div>
      </div>
    </section>
  );
};

export default Menu;
