import React, { useState } from 'react';

const Booking = () => {
  const [formData, setFormData] = useState({
    date: '',
    time: '',
    guests: 1,
  });
  const isLoggedIn = !!localStorage.getItem('token'); // Boolean check for token
  const userEmail = localStorage.getItem('userEmail'); // Retrieve user's email from localStorage, if saved


  const [message, setMessage] = useState('');

  const handleBooking = async (data) => {
    try {
      const token = localStorage.getItem('token'); // Retrieve JWT token
      if (!token) throw new Error('You must log in to make a booking.');

      const response = await fetch('/api/bookings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`, // Include JWT in Authorization header
        },
        body: JSON.stringify(data),
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.message || 'Failed to create booking.');
      }

      return { success: true, message: 'Booking created successfully!' };
    } catch (error) {
      return { success: false, message: error.message };
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    const result = await handleBooking({
      date: formData.date,
      time: formData.time,
      party_size: formData.guests,
    });

    setMessage(result.message);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="booking-page">
      <section className="hero-section d-flex justify-content-center align-items-center">
        <div className="container">
          <h1 className="display-4 text-white">Book Your Table</h1>
          <p className="lead text-white">Reserve your spot at Frankie's Restaurant</p>
          {isLoggedIn ? (
            <p className="text-success">
              Logged in as: {userEmail || 'User'}
            </p>
          ) : (
            <p className="text-warning">
              You are not logged in. Please log in to make reservations.
            </p>
          )}
        </div>
      </section>

      <div className="booking-form-section py-5">
        <div className="container">
          <h2 className="text-center mb-4">Reservation Form</h2>
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6">
                <div className="mb-3">
                  <label htmlFor="date" className="form-label">Reservation Date</label>
                  <input
                    type="date"
                    className="form-control"
                    id="date"
                    name="date"
                    value={formData.date}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="time" className="form-label">Reservation Time</label>
                  <input
                    type="time"
                    className="form-control"
                    id="time"
                    name="time"
                    value={formData.time}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="guests" className="form-label">Number of Guests</label>
                  <input
                    type="number"
                    className="form-control"
                    id="guests"
                    name="guests"
                    value={formData.guests}
                    onChange={handleChange}
                    min="1"
                    required
                  />
                </div>
              </div>
            </div>

            {message && <p className="text-center">{message}</p>}

            <div className="text-center">
              <button type="submit" className="btn btn-primary btn-lg">Confirm Reservation</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Booking;
