import React, { useState, useEffect } from 'react';

const Booking = () => {
  const [bookings, setBookings] = useState([]);
  const [formData, setFormData] = useState({
    date: '',
    time: '',
    guests: 1,
  });
  const [editingBooking, setEditingBooking] = useState(null);
  const [message, setMessage] = useState('');
  const isLoggedIn = !!localStorage.getItem('token');
  const userName = localStorage.getItem('userName');
  const userEmail = localStorage.getItem('userEmail');

  // Fetch bookings and user data on component mount
  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        const response = await fetch('/api/bookings', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch bookings');
        }

        const data = await response.json();
        setBookings(data);
      } catch (error) {
        console.error('Error fetching bookings:', error);
        setMessage('Error fetching bookings. Please try again later.');
      }
    };

    fetchBookings();
  }, []);

  // Handle new booking creation or update
  const handleBooking = async (data) => {
    try {
        const token = localStorage.getItem('token');
        if (!token) {
          setMessage("You are not logged in. Please log in to access bookings.");
          return; // Exit the function if the token is not available
        }

        const response = await fetch('/api/bookings', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        // If response is not JSON, log the response text to understand the issue
        const errorMessage = await response.text();
        console.error('Error fetching bookings:', errorMessage);
        setMessage('Failed to fetch bookings. Please try again later.');
        return;
      }

      const data = await response.json(); // Parse the JSON only if the response is okay
      setBookings(data);



      // Fetch updated bookings after successful creation
      const updatedBookings = await (
        await fetch('/api/bookings', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
      ).json();

      setBookings(updatedBookings);

      return { success: true, message: 'Booking created successfully!' };
    } catch (error) {
      console.error("Booking error:", error);
      return { success: false, message: error.message };
    }
  };

  const handleEditBooking = async (booking) => {
    setEditingBooking(booking);
    setFormData({
      date: booking.date,
      time: booking.time,
      guests: booking.party_size,
    });
  };

  const handleUpdateBooking = async (data) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('You must log in to update a booking.');

      const response = await fetch(`/api/bookings/${editingBooking.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to update booking.';
        throw new Error(errorMessage);
      }

      // Fetch updated bookings after successful update
      const updatedBookings = await (
        await fetch('/api/bookings', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
      ).json();

      setBookings(updatedBookings);
      setEditingBooking(null);
      setFormData({ date: '', time: '', guests: 1 });

      return { success: true, message: 'Booking updated successfully!' };
    } catch (error) {
      console.error('Booking error:', error);
      return { success: false, message: error.message };
    }
  };

  const handleDeleteBooking = async (id) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('You must log in to delete a booking.');

      const response = await fetch(`/api/bookings/${id}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to delete booking.';
        throw new Error(errorMessage);
      }

      // Fetch updated bookings after successful deletion
      const updatedBookings = await (
        await fetch('/api/bookings', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
      ).json();

      setBookings(updatedBookings);

      return { success: true, message: 'Booking deleted successfully!' };
    } catch (error) {
      console.error('Booking error:', error);
      return { success: false, message: error.message };
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    const result = editingBooking
      ? await handleUpdateBooking({
          date: formData.date,
          time: formData.time,
          party_size: formData.guests,
        })
      : await handleBooking({
          date: formData.date,
          time: formData.time,
          party_size: formData.guests,
        });

    setMessage(result.message);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="booking-page">
      <section className="hero-section d-flex justify-content-center align-items-center">
        <div className="container">
          <h1 className="display-4 text-white">Book Your Table</h1>
          <p className="lead text-white">Reserve your spot at Frankie's Restaurant</p>
          {isLoggedIn ? (
            <p className="text-success">
              Logged in as: {userName || userEmail || 'User'}
            </p>
          ) : (
            <p className="text-warning">
              You are not logged in. Please log in to make reservations.
            </p>
          )}
        </div>
      </section>

      <div className="booking-form-section py-5">
        <div className="container">
          <h2 className="text-center mb-4">{editingBooking ? 'Edit Reservation' : 'Reservation Form'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6">
                <div className="mb-3">
                  <label htmlFor="date" className="form-label">Reservation Date</label>
                  <input
                    type="date"
                    className="form-control"
                    id="date"
                    name="date"
                    value={formData.date}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="time" className="form-label">Reservation Time</label>
                  <input
                    type="time"
                    className="form-control"
                    id="time"
                    name="time"
                    value={formData.time}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="guests" className="form-label">Number of Guests</label>
                  <input
                    type="number"
                    className="form-control"
                    id="guests"
                    name="guests"
                    value={formData.guests}
                    onChange={handleChange}
                    min="1"
                    required
                  />
                </div>
              </div>
            </div>

            {message && <p className="text-center">{message}</p>}

            <div className="text-center">
              <button type="submit" className="btn btn-primary btn-lg">
                {editingBooking ? 'Update Reservation' : 'Confirm Reservation'}
              </button>
              {editingBooking && (
                <button
                  type="button"
                  className="btn btn-secondary btn-lg ms-2"
                  onClick={() => {
                    setEditingBooking(null);
                    setFormData({ date: '', time: '', guests: 1 });
                    setMessage('');
                  }}
                >
                  Cancel
                </button>
              )}
            </div>
          </form>
        </div>
      </div>

      {/* Booking List Section */}
      <div className="booking-list-section py-5">
        <div className="container">
          <h2 className="text-center mb-4">Your Bookings</h2>
          <ul className="list-group">
            {bookings.map((booking) => (
              <li key={booking.id} className="list-group-item">
                {booking.date} at {booking.time} for {booking.party_size} guests
                <button
                  className="btn btn-sm btn-warning mx-2"
                  onClick={() => handleEditBooking(booking)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-sm btn-danger"
                  onClick={() => handleDeleteBooking(booking.id)}
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Booking;
