import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Booking = () => {
  const [bookings, setBookings] = useState([]);
  const [formData, setFormData] = useState({ date: '', time: '', party_size: 1 });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBookings = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setMessage('You must be logged in to view or make bookings.');
        navigate('/login');
        return;
      }

      try {
        const response = await fetch('/api/bookings', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch bookings.');
        }

        const data = await response.json();
        setBookings(data);
      } catch (error) {
        setMessage(error.message);
      }
    };

    fetchBookings();
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    const token = localStorage.getItem('token');
    if (!token) {
      setMessage('You must be logged in to create a booking.');
      return;
    }

    try {
      const response = await fetch('/api/bookings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Failed to create booking.');
      }

      const newBooking = await response.json();
      setBookings([...bookings, newBooking]);
      setMessage('Booking created successfully!');
    } catch (error) {
      setMessage(error.message);
    }
  };

  return (
    <div className="booking-page">
      <h1>Booking</h1>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={(e) => setFormData({ ...formData, date: e.target.value })}
          required
        />
        <input
          type="time"
          name="time"
          value={formData.time}
          onChange={(e) => setFormData({ ...formData, time: e.target.value })}
          required
        />
        <input
          type="number"
          name="party_size"
          value={formData.party_size}
          onChange={(e) => setFormData({ ...formData, party_size: parseInt(e.target.value) })}
          required
        />
        <button type="submit">Create Booking</button>
      </form>

      <div className="booking-list">
        <h2>Your Bookings</h2>
        <ul>
          {bookings.map((booking) => (
            <li key={booking.id}>
              {booking.date} at {booking.time} for {booking.party_size} guests
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Booking;
