import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const userName = localStorage.getItem('userName');  // Retrieve user name from localStorage
  const isLoggedIn = !!localStorage.getItem('token'); // Check if user is logged in

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container">
        <Link className="navbar-brand" to="/">
          <img src="/assets/logo.png" alt="Frankie's Logo" height="50" />
        </Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/">Home</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/menu">Menu</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/login">Login</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/signup">Sign Up</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/booking">Booking</Link>
            </li>

            {/* Account Button for Logged-in Users */}
            {isLoggedIn && (
              <li className="nav-item">
                <Link className="nav-link account-btn" to="/account">
                <p className="text-success">
              Logged in as: {userEmail || 'User'}
            </p> {/* Display the user's name */}
                  <i className="bi bi-person-circle"></i> {/* Account icon */}
                </Link>
              </li>
            )}
            {!isLoggedIn && (
              <li className="nav-item">
                <Link className="nav-link" to="/signup">Signup</Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
