import React, { useState } from 'react';

const Booking = () => {
  const [formData, setFormData] = useState({
    date: '',
    time: '',
    guests: 1,
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    try {
      const response = await fetch('/api/bookings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          customer_id: 1, // Replace with logged-in user ID from authentication
          date: formData.date,
          time: formData.time,
          party_size: formData.guests,
        }),
      });

      const result = await response.json();

      if (response.ok) {
        setMessage('Booking confirmed!');
      } else {
        setMessage(result.error || 'Failed to confirm booking.');
      }
    } catch (err) {
      setMessage('An unexpected error occurred.');
    }
  };

  return (
    <div className="booking-page">
      <h1>Book Your Table</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Date</label>
          <input type="date" name="date" value={formData.date} onChange={handleChange} required />
        </div>
        <div>
          <label>Time</label>
          <input type="time" name="time" value={formData.time} onChange={handleChange} required />
        </div>
        <div>
          <label>Guests</label>
          <input type="number" name="guests" value={formData.guests} onChange={handleChange} min="1" required />
        </div>
        {message && <p>{message}</p>}
        <button type="submit">Confirm Booking</button>
      </form>
    </div>
  );
};

export default Booking;
