import React, { useState } from 'react';

const Booking = () => {
  const [formData, setFormData] = useState({
    date: '',
    time: '',
    guests: 1,
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    try {
      const token = localStorage.getItem('token'); // Retrieve JWT from localStorage
      const response = await fetch('/api/bookings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`, // Include token in request
        },
        body: JSON.stringify({
          date: formData.date,
          time: formData.time,
          party_size: formData.guests,
        }),
      });

      const result = await response.json();

      if (response.ok) {
        setMessage('Booking confirmed!');
      } else {
        setMessage(result.error || 'Failed to confirm booking.');
      }
    } catch (err) {
      setMessage('An unexpected error occurred.');
    }
  };


  return (
    <div className="booking-page">
      <section className="hero-section d-flex justify-content-center align-items-center">
        <div className="container">
          <h1 className="display-4 text-white">Book Your Table</h1>
          <p className="lead text-white">Reserve your spot at Frankie's Restaurant</p>
        </div>
      </section>

      <div className="booking-form-section py-5">
        <div className="container">
          <h2 className="text-center mb-4">Reservation Form</h2>
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6">
                <div className="mb-3">
                  <label htmlFor="name" className="form-label">Full Name</label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="email" className="form-label">Email</label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="phone" className="form-label">Phone Number</label>
                  <input
                    type="tel"
                    className="form-control"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="col-md-6">
                <div className="mb-3">
                  <label htmlFor="date" className="form-label">Reservation Date</label>
                  <input
                    type="date"
                    className="form-control"
                    id="date"
                    name="date"
                    value={formData.date}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="time" className="form-label">Reservation Time</label>
                  <input
                    type="time"
                    className="form-control"
                    id="time"
                    name="time"
                    value={formData.time}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="guests" className="form-label">Number of Guests</label>
                  <input
                    type="number"
                    className="form-control"
                    id="guests"
                    name="guests"
                    value={formData.guests}
                    onChange={handleChange}
                    min="1"
                    required
                  />
                </div>
              </div>
            </div>

            {message && <p className="text-center">{message}</p>}


            <div className="text-center">
              <button type="submit" className="btn btn-primary btn-lg">Confirm Reservation</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Booking;
