import React, { useState, useEffect } from 'react';

const Booking = () => {
  const [bookings, setBookings] = useState([]);
  const [formData, setFormData] = useState({ date: '', time: '', party_size: 1 });
  const [message, setMessage] = useState('');

  // Fetch existing bookings when the component loads
  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) throw new Error('You must be logged in.');

        const response = await fetch('/api/bookings', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!response.ok) throw new Error('Failed to fetch bookings.');

        const data = await response.json();
        setBookings(data);
      } catch (error) {
        setMessage(error.message);
      }
    };

    fetchBookings();
  }, []);

  // Handle form submit for booking creation
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('You must be logged in.');

      const response = await fetch('/api/bookings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error('Failed to create booking.');

      const newBooking = await response.json();
      setBookings([...bookings, newBooking]);
      setMessage('Booking created successfully!');
    } catch (error) {
      setMessage(error.message);
    }
  };

  return (
    <div className="booking-page">
      <section className="hero-section">
        <h1 className="text-center">Book Your Table</h1>
        {message && <p className="message">{message}</p>}
      </section>

      <div className="booking-form">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="date">Reservation Date</label>
            <input
              type="date"
              id="date"
              name="date"
              value={formData.date}
              onChange={(e) => setFormData({ ...formData, date: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="time">Reservation Time</label>
            <input
              type="time"
              id="time"
              name="time"
              value={formData.time}
              onChange={(e) => setFormData({ ...formData, time: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="party_size">Number of Guests</label>
            <input
              type="number"
              id="party_size"
              name="party_size"
              value={formData.party_size}
              onChange={(e) => setFormData({ ...formData, party_size: parseInt(e.target.value) })}
              required
            />
          </div>
          <button type="submit">Confirm Reservation</button>
        </form>
      </div>

      <div className="booking-list">
        <h2>Your Bookings</h2>
        <ul>
          {bookings.map((booking) => (
            <li key={booking.id}>
              {booking.date} {booking.time} for {booking.party_size} guests
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Booking;
